//このファイルではstateとして扱っている関数を呼び出す処理をまとめて書いている。
import {createSelector} from 'reselect';

//このてい
const usersSelector = (state) => state.users;

export const getUserId = createSelector(
    [usersSelector],
    state =>state.uid
)
